import React from "react";
import {Link , withRouter } from "react-router-dom";
import MusicLogo from '../assets/img/logo.png';
import './css/Welcome.scss';


class Welcome extends React.Component{

    handleStartClick = () => {
        this.props.history.push("/home"); // "/home" yoluna yönlendirme
    }

    render() {
        return(
            <section id="main">
                <div className="nav-item">
                    <h1 className="navbar-brand">VIRTUAICOMPSER</h1>
                </div>
                <div className="main-row">
                    <div className="main-row-img">
                        <img className="music-logo" src={MusicLogo} alt=""/>
                    </div>
                    <div className="main-row-text">
                        <h1>Music for everyone</h1>
                        <p>Produce the music you want yourself</p>
                        <Link to={"/home"} className="btn" style={{textDecoration: "none"}} onClick={this.handleStartClick}>Start</Link>
                    </div>
                </div>
            </section>
        );
    }
}

export default Welcome;
