import React from "react";
import { Link } from "react-router-dom";
import MusicLogo from "../assets/img/logo.png";
import './css/Home.scss';

class Homepage extends React.Component {
    render() {
        return (
            <section id="main">
                <div className="header">
                    <h1 className="navbar-brand">VIRTUAICOMPSER</h1>
                    <img className="logo" src={MusicLogo} alt="Music Logo" />
                </div>

                <div className="prompt-container">
                    <h2>Ready to create music?</h2>
                    <p>Tell us what you have in mind, and we'll help you compose it!</p>
                    <textarea placeholder="Enter your musical ideas or instructions here..." />
                    <Link to="/compose" className="btn" style={{ textDecoration: "none" }}>
                        Compose Now
                    </Link>
                </div>
            </section>
        );
    }
}

export default Homepage;
