import React from "react";
import MusicLogo from '../assets/img/logo.png';
import './css/Home.scss';

class Home extends React.Component {


    handleSubmit = (event) => {

        console.log("Prompt submitted");
    }

    render() {
        return (
            <div className="home-container">
                <header>
                    <div className="header-content">
                        <h1 className="header-title">VIRTUAICOMPSER</h1>
                        <img className="music-logo" src={MusicLogo} alt="Music Logo" />
                    </div>
                </header>
                <div className="prompt-section">
                    <h2>What's on your mind about music?</h2>
                    <form onSubmit={this.handleSubmit}>
                        <input
                            type="text"
                            value={this.state.promptText}
                            onChange={this.handleChange}
                            placeholder="Share your thoughts..."
                        />
                        <button type="submit">Submit</button>
                    </form>
                </div>
            </div>
        );
    }
}

export default Home;
