import './css/Home.scss';
import {Link} from "react-router-dom";

class Home extends React.Component {


    handleSubmit = (event) => {

        console.log("Prompt submitted");
    }

    render() {
        return (
            <div className="home-container">

                <div className="prompt-container">
                    <h2>Ready to create music?</h2>
                    <p>Tell us what you have in mind, and we'll help you compose it!</p>
                    <textarea placeholder="Enter your musical ideas or instructions here..."/>
                    <Link to="/compose" className="btn" style={{textDecoration: "none"}}>
                        Compose Now
                    </Link>
                </div>
            </div>
        );
    }
}

export default Home;
